mapper.insert:
INSERT INTO product (
    product_name
    , product_category
    , product_price
    , product_writer
    , product_explan
    , product_hot
    , product_ice
    , product_date
) VALUES (
    #{product_name}
    , #{product_category}
    , #{product_price}
    , #{product_writer}
    , #{product_explan}
    , #{product_hot}
    , #{product_ice}
    , CAST(#{product_date} AS DATE)
)

mapper.update:
UPDATE product
SET
    product_index = #{product_index}
  , product_name = #{product_name}
  , product_category = #{product_category}
  , product_price = #{product_price}
  , product_writer = #{product_writer}
  , product_explan = #{product_explan}
  , product_hot = #{product_hot}
  , product_ice = #{product_ice}
  , product_date = CAST(#{product_date} AS DATE)
WHERE
    product_index = #{product_index}
