mapper.insert:
INSERT INTO product_order (
                      order_product_number
                    , order_product_name
                    , order_product_category
                    , order_product_temperature
                    , order_amount
                    , order_price
                    , order_date
                    , order_state
) VALUES (
        #{order_product_number}
         , #{order_product_name}
         , #{order_product_category}
         , #{order_product_temperature}
         , #{order_amount}
         , #{order_price}
         , CAST(#{order_date} AS DATE)
         , #{order_state}
         )

mapper.update:
UPDATE product_order
SET
    order_state =  #{order_state}
WHERE
    order_product_number = #{order_product_number}
